mapper.select:
SELECT BOARD_ID as boardId , MEMBER_ID as memberId, MNAME, WRITE_DATE as writeDate, MODIFY_DATE as modifyDate, TITLE, CONTENT, EVALUATION, HIT FROM BOARD d,MEMBERS m WHERE d.MEMBER_ID=m.mno
AND d.MEMBER_ID=#{memberId}
AND d.CATEGORY_ID=#{category_id}
ORDER BY BOARD_ID DESC LIMIT #{start},#{page}

SELECT 
	count(1)
FROM BOARD d,MEMBERS m
WHERE d.MEMBER_ID=m.mno
 
AND d.MEMBER_ID=#{memberId}

 
AND d.CATEGORY_ID=#{category_id}


AND d.WRITE_DATE=date_format(now(),'%Y%m%d');

mapper.update:
UPDATE BOARD 
SET
	TITLE=#{title},
	CONTENT=#{content}
WHERE
	BOARD_ID=#{boardId}

UPDATE BOARD
SET HIT=HIT+1
WHERE BOARD_ID=#{dno}
